targets.rawSql:
SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(responsetime) as "平均",domain
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip}
GROUP BY time,domain
ORDER BY time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps, domain
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip}
	GROUP BY time,domain
  order by time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, sum(request_length) * 8 / ${cal_interval} as "Maximum request amount"
FROM ${project}
PREWHERE toDate(timestamp) = today() and ${server_ip} and ${domain}
GROUP BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, sum(response_length) * 8 / ${cal_interval} as "Maximum return amount"
	FROM ${project}
	PREWHERE toDate(timestamp) = today() and ${server_ip} and ${domain}
GROUP BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS "Maximun QPS"
	FROM ${project}
	PREWHERE toDate(timestamp) = today() and ${server_ip} and ${domain}
	GROUP BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,countIf(status<400)/count() *100 as "Request success rate"
FROM ${project} 
PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain}
group by time
order by time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, count()/${cal_interval} AS "Current QPS"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain}
	GROUP BY time
  order by time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(responsetime) as "Overall response time"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain}
GROUP BY time
ORDER BY time

SELECT count()/10000 as "Total PV (million)"
FROM ${project}
	PREWHERE toDate(timestamp) = today() and ${server_ip} and ${domain}

SELECT COUNT(DISTINCT client_ip) AS "Total UV"
FROM ${project}
PREWHERE toDate(timestamp) = today() and ${server_ip} and ${domain}

	SELECT toStartOfInterval(timestamp, INTERVAL 60 SECOND) AS time, COUNT(DISTINCT client_ip) AS "每分钟UV"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time
  order by time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS "QPS"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time
  order by time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,
  quantileExact(0.99)(responsetime) as P99
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,
  quantileExact(0.90)(responsetime) as P90
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,
  quantileExact(0.50)(responsetime) as P50
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,
  quantileExact(0.75)(responsetime) as P75
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,
  quantileExact(0.95)(responsetime) as P95
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(responsetime) as "avg"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time
ORDER BY time

	SELECT
	  `top_path` as TOP_Path, COUNT() AS PV, COUNT(DISTINCT client_ip) as "UV",avg(response_length) as "avg return"
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain}
	GROUP BY `top_path`
  order by COUNT() DESC

SELECT
  `top_path` as TOP_Path,avg(responsetime) as "Specify the status response time"
FROM ${project}
PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}
GROUP BY `top_path`

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, upstreamhost, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}
	GROUP BY time,upstreamhost
  order by time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}
	GROUP BY time
  order by time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps, upstreamhost
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time,upstreamhost
  order by time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps, 'Total'
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time
  order by time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(responsetime) as rt,'Total'
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,upstreamhost,avg(responsetime) as rt
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time,upstreamhost
ORDER BY time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, server_ip, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time,server_ip
  order by time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time
  order by time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, server_ip, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time,server_ip
  order by time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, count() / ${cal_interval} AS avgqps,'Total'
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time
  order by time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(responsetime) as rt, 'Total'
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,server_ip,avg(responsetime) as rt
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time,server_ip
ORDER BY time

	SELECT toStartOfHour(timestamp) AS time, count() AS "PV per hour"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time
  order by time

	SELECT toStartOfHour(timestamp) AS time, COUNT(DISTINCT client_ip) AS "UV per hour"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time
  order by time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, sum(request_length) * 8 / ${cal_interval} as "Requests per second"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, sum(response_length) * 8 / ${cal_interval} as "Returns per second"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time
ORDER BY time

SELECT toStartOfMinute(timestamp) AS time, countIf(responsetime > 0.5) AS slow_requests
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain}
GROUP BY time
ORDER BY time

SELECT 
	domain as "user_domain",avg(responsetime) as "resptime", count() as PV,COUNT(DISTINCT client_ip) as UV
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain}
	GROUP BY domain

SELECT
  `path` as "request_path", avg(responsetime) as "avg_resp_time",COUNT() AS PV, COUNT(DISTINCT client_ip) as "UV",avg(response_length) as "avg_return"
FROM ${project}
WHERE $__timeFilter(timestamp) and ${server_ip} and ${domain}
GROUP BY `path`
HAVING PV >100
order by "avg_resp_time" DESC
LIMIT 30;

SELECT
  `path` as "request_path",quantileExact(0.90)(duration) as "P90NGtime",avg(response_length) as "avg_return", avg(responsetime) as "avg_resp_time",COUNT() AS PV, COUNT(DISTINCT client_ip) as "UV"
FROM ${project}
PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain}
GROUP BY `path`
HAVING PV > 10
order by "P90NGtime" DESC
LIMIT 30;

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(responsetime) as "平均",top_path
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time,top_path
ORDER BY time

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, top_path, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time, top_path
  order by time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,quantileExact(0.90)(responsetime) as P90,top_path
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time,top_path
ORDER BY time

SELECT toStartOfMinute(timestamp) AS time, top_path,sum(response_length) as "返回量"
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain}
GROUP BY time, top_path
ORDER BY time

	SELECT
	  referer as Referer, 
    COUNT() as "PV", COUNT(DISTINCT client_ip) as "UV"
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}
	GROUP BY referer
  order by COUNT() DESC
	LIMIT 30;

	SELECT
	  `path` as "request_path", COUNT() AS PV, COUNT(DISTINCT client_ip) as "UV",avg(responsetime) as "avg_resp_time",avg(response_length) as "avg_return"
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}
	GROUP BY `path`
  order by PV DESC
	LIMIT 30;

	SELECT
	  `path` as "request_path",avg(response_length) as "avg_return", avg(responsetime) as "avg_resp_time",COUNT() AS PV, COUNT(DISTINCT client_ip) as "UV"
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}
	GROUP BY `path`
  order by "avg_return" DESC
	LIMIT 30;

	SELECT
	  client_ip as "USERIP", 
    COUNT() AS PV, COUNT(DISTINCT `path`) as "URL Count",sum(response_length) as "Received data volume"
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}
	GROUP BY client_ip
  order by PV DESC
	LIMIT 30;

SELECT client_region as "Province",client_city as "City",avg(responsetime) as "resp_time",count() as "PV",COUNT(DISTINCT client_ip) as UV
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}
group by client_region,client_city

	SELECT
	  client_region, 
    COUNT() AS "unknow"
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}
	GROUP BY client_region

	SELECT
	  client_city, 
    COUNT() AS "unknow"
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}
	GROUP BY client_city

	SELECT
	  client_os_family, 
    COUNT() AS "unknow"
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain}
	GROUP BY client_os_family

	SELECT
	  client_browser_family, 
    COUNT() AS "unknow"
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}
	GROUP BY client_browser_family
  order by COUNT() DESC
	LIMIT 10;

	SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time, client_city, count() / ${cal_interval} AS avgqps
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
	GROUP BY time,client_city
  order by time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,avg(responsetime) as "avg",client_city
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time,client_city
having count()/${cal_interval} > 1
ORDER BY time

SELECT toStartOfInterval(timestamp, INTERVAL ${cal_interval} SECOND) AS time,quantileExact(0.90)(responsetime) as P90,client_city
	FROM ${project}
	PREWHERE $__timeFilter(time) and ${server_ip} and ${domain} and ${status}
GROUP BY time,client_city
having count()/${cal_interval} > 1
ORDER BY time

SELECT timestamp, status, request_method, domain,path,query,server_ip,upstreamhost,responsetime,upstreamtime,request_length,response_length,client_ip,client_city
	FROM ${project}
	PREWHERE $__timeFilter(timestamp) and ${server_ip} and ${domain} and ${status}

order by timestamp desc
limit 500

